public FieldDeserializer getFieldDeserializer_(String key) {
    if (key == null) {
        return null;
    }

    int low = 0;
    int high = sortedFieldDeserializerMap.size() - 1;

    while (low <= high) {
        int mid = (low + high) >>> 1;

        String fieldName = sortedFieldDeserializers[mid].fieldInfo.name;

        int cmp = fieldName.compareTo(key);

        if (cmp < 0) {
            low = mid + 1;
        } else if (cmp > 0) {
            high = mid - 1;
        } else {
            return sortedFieldDeserializers[mid]; // key found
        }
    }

    return null;  // key not found.
